package Homework;
// Write a program that prints the phrase "Knowledge is Power"
public class PP_1_8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Part a: on one line 
		System.out.println("Part a:");
		System.out.println("Knowledge is Power");
		
		/* Part b: on three lines, one word per line, with the 
		 * words centered relative to each other */
		System.out.println(""); // Produces a space for readability
		System.out.println("Part b:");
		System.out.println("Knowledge");
		System.out.println("   is   ");
		System.out.println("  Power  ");
		
		/* Part c: inside a box made up of the
		 * chanracters = and | */ 
		System.out.println(""); // Produces a space for readability 
		System.out.println("Part c:");
		System.out.println("======================");
		System.out.println("| Knowledge is Power |");
		System.out.println("======================");
	}

}
